/* eslint-disable jsx-a11y/anchor-is-valid */
import * as React from 'react'
import classnames from 'classnames'
import logo from '../../images/logo.png'

import styles from './styles.module.css'

function getSortOrderValue(sortOrder) {
  return sortOrder.replace(' ', '').toLowerCase()
}

function getPropertiesDisplayText(count) {
  if (count > 1 || count === 0) {
    return 'properties'
  }
  return 'property'
}

const DefaultState = {
  prodTitle: '',
  priceFrom: '',
  postcode: '',
  sortOrder: '',
  sortOrders: ['Highest First', 'Lowest First']
}

class Filter extends React.Component {
  state = Object.assign({}, DefaultState)

  handleChange = (prop, value) => {
    this.setState({
      [prop]: value
    })
    // console.log(this.state.prodTitle);
  }

  render() {
    const containerClasses = classnames('prod-container', 'flex', styles.container)
    const formClasses = classnames('form-horizontal', styles.form)
    const { priceFrom, postcode, sortOrder, sortOrders, prodTitle } = this.state
    const { postcodes, count, updateFilter } = this.props

    return (

      <div className="top">
        <form
          onChange={() => setTimeout(() => updateFilter(this.state), 0)}
          className={formClasses}
          noValidate
        >
        <div className={containerClasses}>
          <img className="logo" src={logo} alt="logo" />
          <input
            type="search"
            className="searchbar"
            id="prodTitle"
            placeholder="search"
            value={prodTitle}
            onChange={event =>
              this.handleChange('prodTitle', event.target.value)
            }
          />
        </div>
        <div className={containerClasses}>
            <p className="mb-1">
              Refine your results
              <button
                data-cy="clear-button"
                className="ml-1 btn btn-sm"
                type="button"
                onClick={() => {
                  this.setState(Object.assign({}, DefaultState))
                  updateFilter({})
                }}
              >
                Clear
              </button>
            </p>
          </div>

            <h2 data-cy="property-count">
            {`${count} private ${getPropertiesDisplayText(count)} for sale`}
          </h2>
          </form>







      </div>
    )
  }
}

export default Filter
